import { useRef, useState } from "react";

const ManageEditor = ({onCreate}) => {
    /*const [food, setFood] = useState("");
    const [memo, setMemo] = useState("")*/
//이벤트에 대응하는 state
    const [state,setState]=useState({
        food:"",       
        kcal:0,
        memo:"",
        date:""
    });

    const handleChangeState = (e) => {
        setState({
            ...state,
            [e.target.name]:e.target.value
        })
    }

    const handleSubmit = () => {
        onCreate(state.food, state.kcal,state.memo, state.date);
    }
    
    return(
        <div className="ManageEditor">
            <h2>오늘의 식단</h2>
            <form>              
                <div className="Manage-inner">
                    <select>
                        <option>선택</option>
                    </select>
                    <input name="kcal" onChange={handleChangeState} type="number" placeholder="0 kcal" required ></input>
                    <input name="memo" onChange={handleChangeState} placeholder="메모"></input>
                    <p>시간</p>        
                    <button onClick={handleSubmit}>확인</button>            
                </div>
                
            </form>
        </div>
    );
}

export default ManageEditor;
