import { useRef, useState } from "react";

const ManageEditor = () => {
    /*const [food, setFood] = useState("");
    const [memo, setMemo] = useState("")*/

    const [stringState,SetStringState]=useState({
        food:"",
        memo:""
    })
    
    return(
        <div className="ManageEditor">
            
                <h2>오늘의 식단</h2>
                <div>
                    <select>
                        <option>식사</option>
                    </select>
                    <input name="memo" value={stringState.memo} onChange={(e) => {
                        SetStringState({
                            food: stringState.food,
                            memo: e.target.value})
                    }}></input>
                </div>
            
        </div>
    );
}

export default ManageEditor;
