import { useRef, useState } from "react";

const ManageEditor = () => {
    /*const [food, setFood] = useState("");
    const [memo, setMemo] = useState("")*/

    const [state,setState]=useState({
        food:"",
        memo:"",
        kcal:0
    });

    const handleChangeState = (e) => {
        setState({
            ...state,
            [e.target.name]:e.target.value
        })
    }
    
    return(
        <div className="ManageEditor">
            <form>
                <h2>오늘의 식단</h2>
                <div>
                    <select>
                        <option>식사</option>
                    </select>

                    <input name="memo" value={state.memo} onChange={handleChangeState}></input>

                    <input name="kcal" value={state.kcal} onChange={handleChangeState}></input>

                </div>
            </form>
        </div>
    );
}

export default ManageEditor;
