import React, {useState, useRef} from "react"
import ManageEditor from "./ManageEditor";
import ManageList from "./ManageList";
import './App.css';

// const dummylist = [
//   {id:1,
//     food:"치킨",
//     kcal:300,
//     date:new Date().getTime()
//   },
//   {id:2,
//     food:"떡볶이",
//     kcal:200,
